mapper.select:
select 
id        , server_id        , min_latency        , ave_latency        , max_latency        , received        , sent        , outstanding        , zxid        , mode        , node_count        , total_watches        , client_number        , time_stamp        from mario_server_state
   
    
        and id = #{id}
    
    
        and server_id = #{server_id}
    
    
        and min_latency = #{min_latency}
    
    
        and ave_latency = #{ave_latency}
    
    
        and max_latency = #{max_latency}
    
    
        and received = #{received}
    
    
        and sent = #{sent}
    
    
        and outstanding = #{outstanding}
    
    
        and zxid = #{zxid}
    
    
        and mode = #{mode}
    
    
        and node_count = #{node_count}
    
    
        and total_watches = #{total_watches}
    
    
        and client_number = #{client_number}
    
    
        and time_stamp = #{time_stamp}
