mapper.select:
select 
id        , zk_name        , session_timeout        , observer        , observer_auth        from mario_zk_info
   
    
        and id = #{id}
    
    
        and zk_name = #{zk_name}
    
    
        and session_timeout = #{session_timeout}
    
    
        and observer = #{observer}
    
    
        and observer_auth = #{observer_auth}
